package Lect2java;

public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b= 20;
		int temp;
		
		System.out.println("Number before swapping,value of a:"+a+"value of b: "+b);
		
		temp =a;
		a=b;
		b=temp;
		
		System.out.println("Number after swapping,value of a:"+a+" value of b:"+b);

	}

}
